import React from "react";

function DisplayFunction(props) {
  let { data, index, location, icon, tempc, text } = props;
  const { name, region, country } = location;
  if (!location || !name) return <>Loading</>;
  if(index !== 0) {
    icon = data[index].conditionIcon
    tempc = data[index].avgTempC
    text = data[index].forecastedCondition
  }
  return (
    <>
      <div className="icon">
        <img
          src={icon}
          alt="Forecasted Weather Condition"
          height="200px"
          width="200px"
        />
      </div>
      <div className="temperature">
        <span style={{ fontSize: "90px" }}>{tempc}Â°C</span>
        <span style={{ fontSize: "50px" }}>
          {text}
        </span>
      </div>
      <div className="tempfields">
        Date : {data[index].date}
        <br></br>
        {name}, {region}, {country}
        <br></br>Humidity : {data[index].avghum}%<br></br>Wind Speed :{" "}
        {data[index].maxWindKph}kmph
      </div>
    </>
  );
}

export default DisplayFunction;
